func ConnectDatabase() {
	err := godotenv.Load()
	if err != nil {
		log.Fatal("Error loading .env file")
	}

	dsn := fmt.Sprintf(
		"host=%s user=%s password=%s dbname=%s port=%s sslmode=disable",
		os.Getenv("DB_HOST"), os.Getenv("DB_USER"), os.Getenv("DB_PASSWORD"),
		os.Getenv("DB_NAME"), os.Getenv("DB_PORT"),
	)

	db, err := gorm.Open(postgres.Open(dsn), &gorm.Config{})
	if err != nil {
		log.Fatal("Failed to connect to database:", err)
	}

	DB = db
}

func Register(c fiber.Ctx) error {
	var data map[string]string
	if err := c.Bind().Body(&data); err != nil {
		return c.Status(400).JSON(fiber.Map{"error": "Invalid input"})
	}

	// 密码加密
	hashedPassword, err := bcrypt.GenerateFromPassword([]byte(data["password"]), 14)
	if err != nil {
		return c.Status(500).JSON(fiber.Map{"error": "Password hashing failed"})
	}

	user := models.User{
		Username: data["username"],
		Email:    data["email"],
		Password: string(hashedPassword),
	}

	if err := config.DB.Create(&user).Error; err != nil {
		return c.Status(500).JSON(fiber.Map{"error": "Could not create user"})
	}

	return c.JSON(fiber.Map{"message": "User created successfully"})
}

func Login(c fiber.Ctx) error {
	var data map[string]string
	if err := c.Bind().Body(&data); err != nil {
		return c.Status(400).JSON(fiber.Map{"error": "Invalid input"})
	}

	var user models.User
	config.DB.Where("email = ?", data["email"]).First(&user)
	if user.ID == 0 {
		return c.Status(404).JSON(fiber.Map{"error": "User not found"})
	}

	if err := bcrypt.CompareHashAndPassword([]byte(user.Password), []byte(data["password"])); err != nil {
		return c.Status(401).JSON(fiber.Map{"error": "Incorrect password"})
	}

	// 生成 JWT token
	claims := jwt.MapClaims{
		"id":  user.ID,
		"exp": time.Now().Add(time.Hour * 72).Unix(),
	}
	token := jwt.NewWithClaims(jwt.SigningMethodHS256, claims)

	t, err := token.SignedString([]byte(os.Getenv("JWT_SECRET")))
	if err != nil {
		return c.Status(500).JSON(fiber.Map{"error": "Token creation failed"})
	}

	return c.JSON(fiber.Map{"token": t})
}

func CreateComment(c fiber.Ctx) error {
	user := c.Locals("user").(models.User)
	postId, err := strconv.Atoi(c.Params("id"))
	if err != nil {
		return c.Status(400).JSON(fiber.Map{"error": "Invalid post ID"})
	}

	var commentData struct {
		Content string `json:"content"`
	}
	if err := c.Bind().Body(&commentData); err != nil {
		return c.Status(400).JSON(fiber.Map{"error": "Invalid input"})
	}

	comment := models.Comment{
		Content: commentData.Content,
		UserID:  user.ID,
		PostID:  uint(postId),
	}

	if err := config.DB.Create(&comment).Error; err != nil {
		return c.Status(500).JSON(fiber.Map{"error": "Failed to create comment"})
	}

	return c.Status(201).JSON(comment)
}

func GetCommentsByPost(c fiber.Ctx) error {
	postId := c.Params("id")

	var comments []models.Comment
	if err := config.DB.
		Where("post_id = ?", postId).
		Preload("User").
		Order("created_at asc").
		Find(&comments).Error; err != nil {
		return c.Status(500).JSON(fiber.Map{"error": "Failed to fetch comments"})
	}

	return c.JSON(comments)
}

func DeleteComment(c fiber.Ctx) error {
	user := c.Locals("user").(models.User)
	commentId := c.Params("id")

	var comment models.Comment
	if err := config.DB.First(&comment, commentId).Error; err != nil {
		return c.Status(404).JSON(fiber.Map{"error": "Comment not found"})
	}

	if comment.UserID != user.ID {
		return c.Status(403).JSON(fiber.Map{"error": "You can only delete your own comments"})
	}

	if err := config.DB.Delete(&comment).Error; err != nil {
		return c.Status(500).JSON(fiber.Map{"error": "Failed to delete comment"})
	}

	return c.JSON(fiber.Map{"message": "Comment deleted successfully"})
}

