func RequireAuth(c fiber.Ctx) error {
	authHeader := c.Get("Authorization")
	if authHeader == "" || !strings.HasPrefix(authHeader, "Bearer ") {
		return c.Status(fiber.StatusUnauthorized).JSON(fiber.Map{
			"error": "Missing or invalid Authorization header",
		})
	}

	tokenString := strings.TrimPrefix(authHeader, "Bearer ")

	token, err := jwt.Parse(tokenString, func(token *jwt.Token) (interface{}, error) {
		return []byte(os.Getenv("JWT_SECRET")), nil
	})
	if err != nil || !token.Valid {
		return c.Status(fiber.StatusUnauthorized).JSON(fiber.Map{
			"error": "Invalid or expired token",
		})
	}

	claims, ok := token.Claims.(jwt.MapClaims)
	if !ok || !token.Valid {
		return c.Status(fiber.StatusUnauthorized).JSON(fiber.Map{
			"error": "Invalid token claims",
		})
	}

	userId := uint(claims["id"].(float64))

	var user models.User
	if err := config.DB.First(&user, userId).Error; err != nil {
		return c.Status(fiber.StatusUnauthorized).JSON(fiber.Map{
			"error": "User not found",
		})
	}

	// 将 user 保存到 context 里
	c.Locals("user", user)

	return c.Next()
}

func AuthRoutes(app *fiber.App) {
	app.Post("/api/register", controllers.Register)
	app.Post("/api/login", controllers.Login)
}

func ProtectedRoutes(app *fiber.App) {
	app.Get("/api/profile", middleware.RequireAuth, func(c fiber.Ctx) error {
		user := c.Locals("user")
		return c.JSON(fiber.Map{
			"message": "You are authenticated",
			"user":    user,
		})
	})
}

func CommentRoutes(app *fiber.App) {
	app.Post("/api/posts/:id/comments", middleware.RequireAuth, controllers.CreateComment)
	app.Get("/api/posts/:id/comments", controllers.GetCommentsByPost)
	app.Delete("/api/comments/:id", middleware.RequireAuth, controllers.DeleteComment)
}

func PostRoutes(app *fiber.App) {
	app.Post("/api/posts", middleware.RequireAuth, controllers.CreatePost)
	app.Get("/api/posts", controllers.GetPosts)
	app.Delete("/api/posts/:id", middleware.RequireAuth, controllers.DeletePost)
}

func UserRoutes(app *fiber.App) {
	app.Get("/api/me", middleware.RequireAuth, controllers.GetMyProfile)
	app.Get("/api/users/:id", controllers.GetUserProfile)
}
